            ON rctal.customer_trx_id = rcta.customer_trx_id
            AND  rctal.line_type = 'LINE'
         LEFT JOIN wsh_delivery_details wdd
            ON wdd.source_header_id = ooha.header_id
            AND wdd.source_line_id = rctal.interface_line_attribute6
         LEFT JOIN WSH_DELIVERY_ASSIGNMENTS wda
            ON wda.delivery_detail_id = wdd.delivery_detail_id
         LEFT JOIN WSH_NEW_DELIVERIES wnd
            ON wnd.delivery_id =  wda.delivery_id
         LEFT JOIN (SELECT ou.name ou,
                                            ot1.transaction_type_id,
                                            ot1.name ord_type_name,
                                            ot1.description ord_type_desc,
                                            rt.cust_trx_type_id,
                                            rt.name trx_type_name,
                                            rt.description trx_type_desc,
                                            rt.TYPE trx_type,
                                            ot2.start_date_active ord_type_active,
                                            ot2.end_date_active ord_type_end
                                FROM oe_transaction_types_all ot2,
                                          oe_transaction_types_tl ot1,
                                          ra_cust_trx_types_all rt,
                                          hr_operating_units ou
